SHADER.VERTEX:
	// vertex shader
	// ------------------------------------------------------
	// texture diffuse shader per fragment
	// ------------------------------------------------------
	// lights nb: 1
	// textures nb: 1
	// ------------------------------------------------------

	// attributes inputs  (vertex arrays)
		attribute vec3 aVertexPosition;
		attribute vec2 aVertexTexCoord;
		attribute vec3 aVertexNormal;

	// uniform matrices
		uniform mat4 uMVMatrix;
		uniform mat4 uPMatrix;
		uniform mat3 uNMatrix;

	// output
    	varying vec2 vTextureCoord;

	void main(void) {
        gl_Position = uPMatrix * uMVMatrix * vec4(aVertexPosition, 1.0);
        vTextureCoord = aVertexTexCoord;
    }

SHADER.FRAGMENT:
	#ifdef GL_ES
	precision highp float;
	#endif
	// fragment shader
	// ------------------------------------------------------
	// texture diffuse shader per fragment
	// ------------------------------------------------------
	// lights nb: 1
	// textures nb: 1
	// ------------------------------------------------------

	// inputs
    	varying vec2 vTextureCoord;

    // Uniform
    	uniform sampler2D uSampler0;

    vec3 rainbow(float pos) {
        float level = floor((-pos * 0.5 + 0.5) * 6.0);
        float r = float(level <= 2.0) + float(level > 4.0) * 0.5;
        float g = max(1.0 - abs(level - 2.0) * 0.5, 0.0);
        float b = (1.0 - (level - 4.0) * 0.5) * float(level >= 4.0);
        return vec3(r, g, b);
    }

    void main(void) {
		// texture color
		vec4 texColor  = texture2D(uSampler0, vec2(vTextureCoord.s, vTextureCoord.t));
        gl_FragColor = vec4(rainbow(texColor.r * vTextureCoord.s + texColor.g * vTextureCoord.t), 0.85);
    }
